window.storyFormat({
  "name": "Twine to Lua parser",
  "version": "0.0.16",
  "author": "SirBepy",
  "description": "Export your Twine 2 story as a lua file (RobloxStudio)",
  "proofing": false,
  "source": "<html>\r\n\r\n<head>\r\n  <title>{{STORY_NAME}}</title>\r\n  <script type=\"text/javascript\">\r\n    var{isArray}=Array,RegEx={EXTRACT_PROPS:/\\$(?:(\\w+)\\.)?(\\w+)\\s*=\\s*(\"[^\"]+\"|'[^']+'|\\b\\w+\\b|\\d+)/,EXTRACT_CONDITION:/\\?\\((?:(\\w+)\\.)?(\\w+)\\s*(==|<|>)\\s*[\"']?([^\"'\\s]+)[\"']?\\)/,EXTRACT_EMOTION:/\\{\\{([\\w, +]+)\\}\\}/,IS_LINK:/^!*\\[\\[.+?\\]\\]!*$/g,DOUBLE_SQUARE_BRACKETS:/^!*\\[\\[|\\]\\]!*$/g};class Parser{twstory;constructor(twstory){this.twstory=twstory;this.convertPassage=this.convertPassage.bind(this)}toJSON(){console.log('Converting to Story //');var{name,startnode:startNodeId}=this.attrsToObject(this.twstory.attributes),passagesNodes=this.twstory.getElementsByTagName('tw-passagedata'),convertedPassages=[...passagesNodes].map(this.convertPassage),startNode=convertedPassages.find(passage=>passage.pid==startNodeId),passages={};for(const row of convertedPassages)passages[row.name]={...row};return{name,passages,startNode:startNode.name}}convertPassage(passage){var converted=passage.innerHTML.split('\\n').map(line=>this.decodeHTMLEntities(line).trim()).filter(Boolean).reduce((acc,lineRaw)=>{let workingLine=` ${lineRaw}`.slice(1);const propsMatch=workingLine.match(RegEx.EXTRACT_PROPS),entry={},condition=this.getCondition(workingLine),emotions=workingLine.match(RegEx.EXTRACT_EMOTION),linkMatch=workingLine.match(RegEx.IS_LINK);if(propsMatch){acc.props=this.getProp(propsMatch,acc.props);return acc}condition&&(entry.condition=condition);workingLine=workingLine.replace(RegEx.EXTRACT_CONDITION,'');emotions&&(entry.emotions=emotions[1].split(',').map(t=>t.trim()).filter(Boolean));workingLine=workingLine.replace(RegEx.EXTRACT_EMOTION,'');if(linkMatch){const linkContent=workingLine.replace(RegEx.DOUBLE_SQUARE_BRACKETS,'').split('|');if(linkContent&&linkContent.length==1){entry.text=null;entry.link=linkContent[0];acc.redirects.push(entry);return acc}if(linkContent&&linkContent.length==2){entry.text=linkContent[0];entry.link=linkContent[1];acc.responses.push(entry);return acc}return acc}entry.text=workingLine;acc.lines.push(entry);return acc},{lines:[],responses:[],redirects:[],props:{}}),{name,pid,tags}=this.attrsToObject(passage.attributes);return{...converted,tags:tags.split(' ').filter(Boolean),name,pid}}getProp(matchResult,props){var[,groupName,propName,value]=matchResult;let typedValue;try {typedValue=JSON.parse(value)} catch {typedValue=value}groupName?(props[groupName]=props[groupName]||{},props[groupName][propName]=typedValue):(props[propName]=typedValue);return props}getCondition(text){var match=text.match(RegEx.EXTRACT_CONDITION);if(!match)return null;let[,group,prop,comparator,value]=match;switch(comparator) {case '==':comparator='eq';break;case '>':comparator='gt';break;case '<':comparator='lt';break;case '>=':comparator='gte';break;case '<=':comparator='lte';break}try {value=JSON.parse(value)} catch {}return{group,prop,comparator,value}}toLuaTableString(){var parseLua=value=>{if(value==null)return null;if(isArray(value)){const elements=value.map(parseLua).join(', ');return`{${elements}}`}const valueType=typeof value;if(valueType=='number')return`${value}`;if(valueType=='string')return JSON.stringify(value);if(valueType=='object'){const properties=Object.entries(value).map(([key,val])=>`${key} = ${parseLua(val)}`).join(', ');return`{${properties}}`}throw Error(`Unsupported data type: ${valueType}`)};return`return ${parseLua(this.toJSON())}`}attrsToObject(node){return Object.entries(node).reduce((acc,[key,value])=>({...acc,[value.name]:value.value}),{})}decodeHTMLEntities(text){var textArea=document.createElement('textarea');textArea.innerHTML=text;return textArea.value}}window.parseTwineToLua=()=>{var twStorydata=document.getElementsByTagName('tw-storydata')[0];var parser=new Parser(twStorydata);document.getElementById('output').innerHTML=parser.toLuaTableString()};\r\n  </script>\r\n  <style>\r\n    body,\r\n    html {\r\n      box-sizing: border-box;\r\n      margin: 0;\r\n      padding: 0;\r\n      background-color: #365486;\r\n      color: #7FC7D9;\r\n    }\r\n\r\n    body {\r\n      display: flex;\r\n      flex-direction: column;\r\n      justify-content: center;\r\n      align-items: center;\r\n      height: 100%;\r\n    }\r\n\r\n    #storyData {\r\n      display: none;\r\n    }\r\n\r\n    #output {\r\n      width: 500px;\r\n      height: 500px;\r\n      max-width: 80vw;\r\n      max-height: 80vh;\r\n      overflow-y: auto;\r\n      text-wrap: wrap;\r\n      opacity: 0.5;\r\n      padding: 1em;\r\n      border: #0F1035 4px solid;\r\n      border-radius: 10px;\r\n    }\r\n\r\n    button {\r\n      width: 180px;\r\n      height: 40px;\r\n      border-radius: 10px;\r\n      font-size: 1.2em;\r\n      background-color: #7FC7D9;\r\n      color: white;\r\n      border: #DCF2F1 1px solid;\r\n      transition: 0.2s;\r\n      cursor: pointer;\r\n    }\r\n\r\n    button:hover {\r\n      opacity: 0.5;\r\n    }\r\n\r\n    button:active {\r\n      opacity: 0.2;\r\n    }\r\n\r\n    button:disabled {\r\n      opacity: 1;\r\n    }\r\n  </style>\r\n</head>\r\n\r\n<body>\r\n  <button onclick=\"copyText(this)\">Copy output</button>\r\n  <pre id=\"output\"></pre>\r\n  <div id=\"storyData\">\r\n    {{STORY_DATA}}\r\n  </div>\r\n  <script>\r\n    function copyText() {\r\n      const textToCopy = document.getElementById(\"output\").innerText;\r\n      const textarea = document.createElement(\"textarea\");\r\n      textarea.value = textToCopy;\r\n\r\n      document.body.appendChild(textarea);\r\n      textarea.select();\r\n      document.execCommand('copy');\r\n      document.body.removeChild(textarea);\r\n\r\n      const button = document.querySelector('button');\r\n      button.textContent = 'Copied to clipboard';\r\n      button.disabled = true\r\n\r\n      setTimeout(() => {\r\n        button.textContent = 'Copy output';\r\n        button.disabled = false\r\n      }, 1500);\r\n    }\r\n    parseTwineToLua();\r\n  </script>\r\n</body>\r\n\r\n</html>\r\n"
});